mapper.select:
SELECT fieldValue,
    tagName,
    tagValue,
    timestampInEpoch
FROM ${tableName}
WHERE applicationName = #{applicationName}
    AND metricName = #{metricName}
    AND fieldName = #{fieldName}

        AND tagName = #{tag.tagName} AND tagValue = #{tag.tagValue}

    AND timestampInEpoch BETWEEN #{range.from} AND #{range.to}

        AND timestampInEpoch % #{interval} = 0

LIMIT ${limit}
